mapper.update:
INSERT
INTO
fran_notice(fran_notice_num, fran_applidoc_code, fran_code, fran_notice_title, emp_no, emp_name, fran_notice_date)
VALUES
	(
		  NOTICE_SEQ.NEXTVAL
		, #{fran_applidoc_code}
		, #{fran_code}
		, (SELECT substr(FRAN_ADDR, 1,6) FROM FRAN WHERE FRAN_CODE = #{fran_code})||' 게시글'
		, #{emp_no}
		, #{emp_name}
		, sysdate
	)
