mapper.select:
select emp_name, emp_tel 
from hq_emp 
where fran_code = #{fran_code}

mapper.update:
insert into fran_st_change (
ST_CHANGE_REASON , ST_CHANGE_DATE , APPLI_ST , ST_CHANGE_START , ST_CHANGE_END , ST_CHANGE_CODE , FRAN_CODE
) values (#{st_change_reason}, sysdate, #{appli_st}, #{st_change_start}, #{st_change_end}, 'ST'||lpad(franstchange_seq.nextval, 4, '0'), #{fran_code})
