mapper.select:
SELECT e.logis_safe_stock, e.logis_suit_stock, e.logis_goods_stock, e.wh_name, f.sup_goods_name, f.sup_goods_smca, f.sup_goods_laca, d.goods_max_order, d.hq_goods_code, e.logis_code FROM logis_wh e, hq_goods d, sup_goods f WHERE d.hq_goods_code = e.hq_goods_code AND f.sup_goods_code = d.sup_goods_code AND e.logis_code = #{keywordMap.logis_code}
and e.wh_name = #{keywordMap.wh}
and f.sup_goods_laca = #{keywordMap.laca}
and f.sup_goods_smca = #{keywordMap.smca}
and f.sup_goods_name like '%'||#{keywordMap.keyword}||'%'

SELECT count(*) FROM logis_wh d, logis c, hq_goods e, sup_goods f WHERE c.logis_code is not null AND c.logis_code = d.logis_code AND e.hq_goods_code = d.hq_goods_code AND f.sup_goods_code = e.sup_goods_code AND c.logis_code = #{keywordMap.logis_code}
AND d.wh_name = #{keywordMap.wh}
AND f.sup_goods_laca = #{keywordMap.laca}
AND f.sup_goods_smca = #{keywordMap.smca}
AND f.sup_goods_name like '%'||#{keywordMap.keyword}||'%'

		SELECT
			* 
		FROM
			logis_wh 
		WHERE
			hq_goods_code=#{hq_goods_code}
        AND wh_name=#{wh_name}
        and logis_code = #{logis_code}

mapper.update:
UPDATE
logis_wh
SET
logis_goods_stock = #{logis_goods_stock}, logis_safe_stock = #{logis_safe_stock}, logis_suit_stock = #{logis_suit_stock}
WHERE
hq_goods_code = #{hq_goods_code}
AND
wh_name = #{wh_name}
AND logis_code = #{logis_code}

mapper.select:
SELECT TBLA.*, TBLB.* FROM ( SELECT DISTINCT K.TAKING_CODE ,K.WH_CODE ,J.WH_NAME ,TO_CHAR(TAKING_DATE, 'YYYY-MM-DD') TDATE ,K.TAKING_ST ST_NUM FROM LOGIS_STOCK_TAKING K ,LOGIS_WH J ,HQ_GOODS L WHERE J.LOGIS_CODE = K.LOGIS_CODE AND J.WH_CODE = K.WH_CODE AND L.HQ_GOODS_CODE = K.HQ_GOODS_CODE AND L.HQ_GOODS_CODE = J.HQ_GOODS_CODE AND K.LOGIS_CODE = #{keywordMap.logis_code}
AND j.wh_name = #{keywordMap.wh}
AND l.hq_goods_name like '%'||#{keywordMap.keyword}||'%'
ORDER by TDATE DESC )TBLA, ( SELECT COM_GROUP_CODE , COM_CODE ST_NUM , COM_NAME ST FROM COM_CODE WHERE COM_GROUP_CODE = 'TAKING_ST')TBLB WHERE TBLA.ST_NUM = TBLB.ST_NUM

SELECT count(DISTINCT K.TAKING_CODE) FROM logis_stock_taking k ,logis_wh j ,hq_goods l WHERE j.logis_code = k.logis_code AND j.wh_code = k.wh_code AND l.hq_goods_code = k.hq_goods_code AND l.hq_goods_code = j.hq_goods_code and k.logis_code = #{keywordMap.logis_code}
and j.wh_name = #{keywordMap.wh}
and l.hq_goods_name like '%'||#{keywordMap.keyword}||'%'
ORDER by k.taking_date desc

mapper.update:
    UPDATE LOGIS_STOCK_TAKING SET 
		COMPUTER_STOCK = #{comquan}, ACTUAL_STOCK = #{realquan}, DEFF_QUANTITY = #{realquan}-#{comquan}, ADJUST_QUANTITY = #{realquan}-#{comquan}
	WHERE 
		TAKING_CODE = #{takingCode}
    AND HQ_GOODS_CODE = #{goodsCode}
    AND LOGIS_CODE = #{logis_code}
    AND Wh_CODE = #{wh_code}

UPDATE LOGIS_STOCK_TAKING SET
TAKING_ST = 1
WHERE TAKING_CODE = #{takingCode}
